handlers.handler:
const kKE = Components.interfaces.nsIDOMKeyEvent;
switch (event.keyCode) {
    case kKE.DOM_VK_PAGE_UP:
        this.moveView(-1);
        break;
    case kKE.DOM_VK_PAGE_DOWN:
        this.moveView(1);
        break;
}

if (event.shiftKey) {
    this.moveView(event.detail < 0 ? -1 : 1);
}

// Prevent default scroll handling
event.preventDefault();
